import numpy as npimport matplotlib.pyplot as plt# New figure (increase figure size)fig = plt.figure(figsize=(15,8))# Bigger fontplt.rcParams.update({'font.size': 30})# Original datax = [1,2,3,4];y = [5.5,7.0,9.5,9.9];# Make a plotplt.plot(x,y,'x', markerSize = 20, markerEdgeWidth = 3)plt.xlabel('x')plt.ylabel('y')# Polyfit p = np.polyfit(x, y, 3)# Fit linear x1 = np.linspace(0,5,100)f = np.polyval(p,x1)# Add to plotplt.plot(x1,f,'-', lineWidth=3)# Tighter marginsplt.tight_layout()# Set your own filename/directory herefname="fit_cubic.png"fdir="/Users/Chris/Code/makecourse/modules/MAS2806-PHY2039/static/images/week2/"# Save the filefig.savefig(fdir+fname, facecolor=(1,1,1,0.8), transparent=True)